(56, '/Resources/views/', 'main.html.twig', '{% extends ''@Site/_layout.html.twig'' %}\r\n\r\n{% block content %}\r\n    <!-- Homepage Slider -->\r\n    <div class="homepage-slider">\r\n        {{ homepage_slider }}\r\n    </div>\r\n    <!-- End Homepage Slider -->\r\n\r\n    <!-- Press Coverage -->\r\n    {#\r\n    <div class="section">\r\n        <div class="container">\r\n            <div class="row">\r\n                <div class="col-md-4 col-sm-6">\r\n                    <div class="in-press press-wired">\r\n                        <a href="#">Morbi eleifend congue elit nec sagittis. Praesent aliquam lobortis tellus, nec consequat vitae</a>\r\n                    </div>\r\n                </div>\r\n                <div class="col-md-4 col-sm-6">\r\n                    <div class="in-press press-mashable">\r\n                        <a href="#">Morbi eleifend congue elit nec sagittis. Praesent aliquam lobortis tellus, nec consequat vitae</a>\r\n                    </div>\r\n                </div>\r\n                <div class="col-md-4 col-sm-6">\r\n                    <div class="in-press press-techcrunch">\r\n                        <a href="#">Morbi eleifend congue elit nec sagittis. Praesent aliquam lobortis tellus, nec consequat vitae</a>\r\n                    </div>\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n    <!-- Press Coverage -->\r\n    #}\r\n\r\n    <!-- Services -->\r\n    <div class="section">\r\n        <div class="container">\r\n            <div class="row">\r\n                <div class="col-md-4 col-sm-6">\r\n                    <div class="service-wrapper">\r\n                        <img src="{{ asset(''images/Step1.png'') }}" alt="Step 1"> &nbsp;&nbsp;&nbsp;&nbsp;\r\n                        <img src="{{ asset(''bundles/site/img/service-icon/diamond.png'') }}" alt="Service 1">\r\n                        <h3>Поиск вакансии</h3>\r\n                        <p>Успешное прохождение собеседования специалистом, который будет постоянно вести диалог с работодателем. </p>\r\n                        <a href="#" class="btn">Read more</a>\r\n                    </div>\r\n                </div>\r\n                <div class="col-md-4 col-sm-6">\r\n                    <div class="service-wrapper">\r\n                        <img src="{{ asset(''images/Step2.png'') }}" alt="Step 2"> &nbsp;&nbsp;&nbsp;&nbsp;\r\n                        <img src="{{ asset(''bundles/site/img/service-icon/ruler.png'') }}" alt="Service 2">\r\n                        <h3>Разделение работы...</h3>\r\n                        <p>среди участников, имеющих соответствующий уровню <br> сложности задач рейтинг.</p>\r\n                        <a href="#" class="btn">Read more</a>\r\n                    </div>\r\n                </div>\r\n                <div class="col-md-4 col-sm-6">\r\n                    <div class="service-wrapper">\r\n                        <img src="{{ asset(''images/Step3.png'') }}" alt="Step 3"> &nbsp;&nbsp;&nbsp;&nbsp;\r\n                        <img src="{{ asset(''bundles/site/img/service-icon/box.png'') }}" alt="Service 3">\r\n                        <h3>Успешное выполнение</h3>\r\n                        <p>работы и наличие возможностей для конвейерной работы с несколькими работодателями.</p>\r\n                        <a href="#" class="btn">Read more</a>\r\n                    </div>\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n    <!-- End Services -->\r\n\r\n    <!-- Call to Action Bar -->\r\n    <div class="section section-white">\r\n        <div class="container">\r\n            <div class="row">\r\n                <div class="col-md-12">\r\n                    <div class="calltoaction-wrapper">\r\n\r\n                        <p>\r\n                        Наш сайт создан для объединения знаний и навыков всех желающих найти оплачиваемую работу.  Проект для совместного поиска вакансий и выполнения работы.\r\n                        Условия сотрудничества равноправные, бюджеты вакансий будут открытыми.\r\n                        Чем больше присоединится желающих, тем шире область наших знаний и соответственно возможностей.\r\n                        В идеале требуется дойти до уровня, когда каждый участник сможет развивать свои самые сильные\r\n                        стороны в поиске заказов или выполнять тот вид работы, в котором является профессионалом.\r\n                        </p>\r\n\r\n                        <p>\r\n                        У каждого человека есть свои сильные стороны.\r\n                        К сожалению не всем удаётся попасть в нужную среду, максимально реализовать весь свой потенциал и посвятить себя любимому делу.\r\n                        Только вместе мы способны расчитывать на спокойное и качественное решение любой задачи.\r\n                        Приглашаем к сотрудничеству всех желающих!\r\n                        </p>\r\n                        <p>\r\n                            Особенностью ресурса следует назвать отсутствие начальников и подчинённых.\r\n                            Участники действуют в рамках личных инициатив и доверия со стороны зарегистрированных пользователей.\r\n                        </p>\r\n\r\n                        {#<h3>It''s a free multipurpose Bootstrap 3 template!</h3> <a href="http://www.dragdropsite.com" class="btn btn-orange" target="_blank">Download here!</a>#}\r\n                    </div>\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n    <!-- End Call to Action Bar -->\r\n\r\n    <!-- Testimonials -->\r\n    <div class="section">\r\n        <div class="container">\r\n            <h2>Участники проекта</h2>\r\n            <div class="row">\r\n                <!-- Testimonial -->\r\n                <div class="testimonial col-md-4 col-sm-6">\r\n                    <!-- Author Photo -->\r\n                    <div class="author-photo">\r\n                        <!--<img src="{{ asset(''